mapper.update:

update movies set 
movie_cnt = movie_cnt+1 
where movie_seq = #{movie_seq}

mapper.select:
select DISTINCT m.movie_seq as movie_seq , m.movie_title as movie_title , m.movie_img as movie_img from movies m,members u,history h where m.movie_seq = h.movie_seq and u.mb_id = h.mb_id
and h.mb_id = #{mb_id}
group by m.movie_seq, m.movie_title, m.movie_img order by m.movie_seq desc limit 5

mapper.insert:

INSERT INTO history (
		mb_id
	  , movie_seq
	  , history_date
	   ) VALUES(
	    #{mb_id}
	  , #{movie_seq}
	  , DATE_FORMAT(SYSDATE(), '%Y-%m-%d %H:%i:%s')
 	)
